function ResultCompile {
    param(
        [string]$Project,
        [string]$FileLocation
        )
    $Result = ManageCompile -Project $Project -FileLocation $FileLocation
    if( $Result -eq 0 ) {
	    return "Build executed successfully" }
    if( $Result -eq 1 ) {
	    return "Build executed successfully with warnings"} 
    if( $Result -eq 3 ) {
	    return "Build failed"}
}

function ManageCompile {
    param(
        [string]$Project,
        [string]$FileLocation
        )
    New-Item $FileLocation\Diagnosis\$Project.txt -ItemType File -Force
    $Result = BRcompile -Project $Project -FileLocation $FileLocation
    return $Result
}

function BRcompile {
    param(
        [string]$Project,
        [string]$FileLocation
        )

    $Build = Start-Process -FilePath "D:\Ersa\Tools\BrAutomation\AS49\Bin-en.\BR.AS.Build.exe" -ArgumentList "$FileLocation\Ersa_Maschine.apj -c $Project -o $FileLocation\Binaries -t $FileLocation\Temp -all -buildRUCPackage" -RedirectStandardOutput "$FileLocation\Diagnosis\$Project.txt" -WindowStyle Hidden -Wait -PassThru            
    return $Build.ExitCode

}
function ConvertResult {
    param(
        [string]$Project,
        [string]$FileLocation
        )

    return Get-Content -Path $FileLocation\Diagnosis\$Project.txt -Tail 2

}

function ResetRepo {
    param(
