    Copy-Item -Path $FileLocation\Flashcard_Ordner_User\All\BR\mappView\Package.pkg                            -Destination $FileLocation\Logical\mappView
    Copy-Item -Path $FileLocation\Flashcard_Ordner_User\All\BR\mappView\Widgets\Package.pkg                    -Destination $FileLocation\Logical\mappView\Widgets
